import React from "react";
import { View, Text, Image, TextInput, TouchableOpacity, ImageBackground } from "react-native";
import { AntDesign } from "@expo/vector-icons";


export default function Getstarted(params) {
  const navigation = params.navigation;
  return (
    <View>
    <ImageBackground source = {require("../images/thread.png")}
    
      style ={{
        flex: 1,
        resizeMode: "cover",
        width:"100%",
        height: "100%",

      }}
      ></ImageBackground>
      
      <Text style={{ fontSize: 30, color: "grey" }}>WELCOME TO</Text>
      <Text style={{ fontSize: 40, fontWeight: "bold" }}>THE THREAD STORE</Text>

      <TouchableOpacity
        onPress={() => {
          navigation.navigate("Login");
        }}
        style={{
          padding: 15,
          paddingHorizontal: 80,
          marginTop: 15,
          alignItems: "center",
          borderRadius: 10,
          flexDirection: "row",
          backgroundColor: "black",
        }}
      >
        <AntDesign name="apple1" size={24} color="white" />
        <Text style={{ paddingLeft: 10, color: "white" }}>Get Started</Text>
      </TouchableOpacity>
    </View>
  );
 
  }
